import { loadConfig } from "@babel/core/lib/config/files";
import { eventWrapper } from "@testing-library/user-event/dist/utils";
import React, {useState} from "react";

function Addnote() {
    const[Title, setTitle] = useState("");
    const[note, setnote] = useState("");
    const[items, setitem] = useState([]);
    function titleChange(event) {
        const addtitle = event.target.value;
        setTitle(addtitle);
    }
    function noteChange(event) {
        const addnote = event.target.value;
        setitem(addnote);
    }
    function add(event) {
      event.preventDefault();
        const Note = {noteTitle : {Title},noteData : {note} };
        setitem( (prevVal)=>{return [...prevVal, Note] });
         setTitle("");
         setnote("");
         console.log(Note);
        
    }
    function createNote(Note) {
        setitem((Note) =>{
            item.push(Note);
        })
        console.log(item);
    }
    return (
        <div className="Title">
        <form>
            <input type="title" onChange ={titleChange} name=" Title" placeholder="Title"></input>
            <p><textarea name=" Note" onChange = {noteChange} placeholder="Take a note.."></textarea></p>
            <button onClick={add} type="submit">Add</button>
        </form>
         </div>

    );
}
export default Addnote;
