import React, {useState} from "react";

function AddnoteArea(props) {
    var[note, setNote] = useState({
        title:"",
        content:""
    });

    function Change(event) {
        const {name, value} = event.target;
        setNote( prevNote=>{
            return {...prevNote, [name] : value};
    });
    }

    function add(event) {
      props.onAdd(note);  
      event.preventDefault();

    }

    return (
        <div className="TitleClass">
        <form>
            <input  onChange ={Change} name=" title" placeholder="Title" value={note.title}></input>
            <p><textarea name=" content" onChange = {Change} placeholder="Take a note..." value={note.content}></textarea></p>
            <button onClick={add} type="submit">Add</button>
        </form>
         </div>

    );
}
export default AddnoteArea;
